import App from 'next/app'
import React from 'react'
import { ThemeProvider } from 'styled-components'

const theme = {
  colors: {
    primary: '#0070f3',
    secondary: 'red',
    highlight: 'blue',
    success: 'green',
    warning: 'yellow',
    error: 'red'
  },
  sizes: {
    rowHeight: '20px',
    itemWidth: '100px'
  },
}

export default class MyApp extends App {
  render() {
    const { Component, pageProps } = this.props
    return (
      <ThemeProvider theme={theme}>
        <Component {...pageProps} />
      </ThemeProvider>
    )
  }
}
